package conditionals;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sayi = 24;
		
		if (sayi < 20) {
			System.out.println("Sayi 20den küçük");
		}
		else if (sayi == 20) {
			System.out.println("Sayi 20y eþittir");
		}
		else {
			System.out.println("Sayi 20den büyüktür");
		}

	}

}
